import React, {useState, useEffect} from 'react'
import axios from 'axios'
import { Card, Container, Col, Row, Form, Button } from 'react-bootstrap'

const token = localStorage.getItem('token')

const AdminDashboard = () => {
    const [users, setUsers] = useState()
    useEffect(() => {
        const fetchData = () => {
            axios.get('https://secopsapi.herokuapp.com/api/users', {headers: {authorization: `Bearer ${token}`}}).then((res) => {
            setUsers(res.data.message)

        })} 
        fetchData()
    }, [users])

    const submitHandler = (e) => {
        e.preventDefault()
    }

    if (!users) {
        return (
            <div className="py-3">
                <Container>
                    <h1 className="text-center">Club Members</h1>
                </Container>
            </div>
            
        )
    }else {
        users.map((user) => {
            
            user.rangeMeetings = user.meetingsAttended
        })
        const changeMeeting = (e) => {
            users.rangeMeetings = e.target.value
        }

        
        
        return (
            
            <div className="py-3">
                <Container>
                    <h1 className="text-center" style={{"paddingBottom": "3vh"}}>Club Members</h1>
                    <Row>
                    {users.map((user, idx) => {
                        return (
                        <Col xs="auto" style={{"margin": '0 auto'}}>
                            <Card style={{ width: '35vw', "marginBottom": "2vw"}}>
                                <Card.Img variant="top" src="https://external-content.duckduckgo.com/iu/?u=http%3A%2F%2Fwww.vanasconstruction.com%2Fwp-content%2Fuploads%2F2014%2F10%2FPicture-1-LHS-NIGHT_ENTRANCE.jpg&f=1&nofb=1" />
                                <Card.Body>
                                    <Card.Title>{idx} Name: {user.firstName} {user.lastName}</Card.Title>
                                    <Card.Subtitle className="mb-2 text-muted">Student ID: {user.studentID}</Card.Subtitle>
                                    <Card.Subtitle className="mb-2 text-muted">Email: {user.email}</Card.Subtitle>
                                    <Card.Text>
                                        Graduating in {user.gradYear}!<br />
                                        Total meetings attended: {user.meetingsAttended}<br /><br />
                                       
                                        Years in club: {user.yearsInClub} <br />
                                        {user.leadershipPosition ? "Leadership position: " + user.leadershipPosition : ""}
                                    </Card.Text>
                                    <Form onSubmit={submitHandler}>
                                        <Form.Group controlId="formBasicRangeCustom">
                                            <Form.Label>Change Meetings Attended</Form.Label>
                                            <Form.Control type="text" placeholder="Meetings Attended" onSubmit={changeMeeting}></Form.Control>
                                            
                                        </Form.Group>
                                        <Button>Change</Button> 
                                    </Form> 
                                </Card.Body>
                               
                            </Card>
                        </Col>
                        )
                     })}
                    </Row>
                </Container>
            </div>
        )
    }


   

    
}

export default AdminDashboard
