import React, {useState} from 'react'
import {Form, Button} from 'react-bootstrap'
import axios from 'axios'

const RegistrationScreen = () => {
    let [studentID, changeID] = useState('')  
    let [firstName, changeFirstName] = useState('')  
    let [lastName, changeLastName] = useState('') 
    let [email, changeEmail] = useState('')
    let [phoneNumber, changePhoneNumber] = useState('')
    let [gradYear, changeGradYear] = useState('2021')
    let [discordID, changeDiscordID] = useState('')
    let [password, changePassword] = useState('')

    const idListener = (e) => {
        changeID(e.target.value)
    }
    const firstNameListener = (e) => {
        changeFirstName(e.target.value)
    }
    const lastNameListener = (e) => {
        changeLastName(e.target.value)
    }
    const passwordListener = (e) => {
        changePassword(e.target.value)
    }
    const discordIDListener = (e) => {
        changeDiscordID(e.target.value)
    }
    const emailListener = (e) => {
        changeEmail(e.target.value)
    }
    const phoneNumberListener = (e) => {
        changePhoneNumber(e.target.value)
    }
    const gradYearListener = (e) => {
        changeGradYear(e.target.value)
    }
    const onSubmit = (e) =>{
        if (!studentID || !firstName || !lastName || !email || !password || !discordID){
            return
        }
        try{
            console.log('hi')
            axios.post('http://localhost:7777', {
                headers: {"studentID": studentID, "firstName": firstName, "lastName": lastName, "email": email, "phoneNumber": phoneNumber, "gradYear": gradYear}
            })
        }

        catch (error){
            console.log(error)
        }
        
    } 
    return (
        <Form>
            <Form.Group controlId="formBasicText">
                <Form.Label>Student ID</Form.Label>
                <Form.Control onChange={idListener} type="text" placeholder="Enter ID" required/>
            </Form.Group>
            <Form.Group controlId="formBasicText">
                <Form.Label>First Name</Form.Label>
                <Form.Control onChange={firstNameListener} type="text" placeholder="Enter Name" required/>
            </Form.Group>
            <Form.Group controlId="formBasicText">
                <Form.Label>Last Name</Form.Label>
                <Form.Control onChange={lastNameListener}type="text" placeholder="Enter Name" required/>
            </Form.Group>
            <Form.Group controlId="formBasicText">
                <Form.Label>Password</Form.Label>
                <Form.Control onChange={passwordListener} type="text" placeholder="Enter Password" required/>
            </Form.Group>
            <Form.Group controlId="formBasicText">
                <Form.Label>Discord ID (Name and 4 digits)</Form.Label>
                <Form.Control onChange={discordIDListener} type="text" placeholder="Enter Name" required/>
            </Form.Group>
            <Form.Group controlId="formBasicEmail">
                <Form.Label>Email address</Form.Label>
                <Form.Control onChange={emailListener} type="email" placeholder="Enter email" required/>
            </Form.Group>
            <Form.Group controlId="formBasicText">
                <Form.Label>Phone Number (optional)</Form.Label>
                <Form.Control onChange={phoneNumberListener} type="text" placeholder="Phone Number"/>
            </Form.Group>
            <Form.Group controlId="RegistrationForm.ControlSelect1">
                <Form.Label>Graduation Year</Form.Label>
                <Form.Control onChange={gradYearListener} as="select">
                    <option>2021</option>
                    <option>2022</option>
                    <option>2023</option>
                    <option>2024</option>
                </Form.Control>
            </Form.Group>
            <Form.Group>
                <Form.Label>Do you have any experience with Cybersecurity? (Experience Not Required)</Form.Label>
                <Form.Check type="radio" name="radios" label="Yes" value="yes" id="formExperienceYes"/>         
                <Form.Check type="radio" name="radios" label="No" value="no" id="formExperienceNo"/>
            </Form.Group>
            <Form.Group controlId="RegistrationForm.ControlTextarea1">
                <Form.Label>If you answered yes to the previous question, please elaborate on your experience (what you have learned)</Form.Label>
                <Form.Control as="textarea" rows={6}/>
            </Form.Group>
            <Form.Group controlId="RegistrationForm.ControlTextarea2">
                <Form.Label>Why do you want to join the Security Ops Club?</Form.Label>
                <Form.Control as="textarea" rows={6} required/>
            </Form.Group>
            <Button variant="primary" size="lg" type="submit" onClick={onSubmit}>
                Register
            </Button>
        </Form>
    )
}

export default RegistrationScreen
